import React, { useState } from "react";
import { Navigate } from "react-router-dom";

function Form() {
  const [company, setCompany] = useState();
  const [country, setCountry] = useState();
  const [city, setCity] = useState();
  const [salary, setSalary] = useState();

  const navigate = Navigate;

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:3000/form", { company, country, city, salary })
      .then((result) => {
        console.log(result);
        navigate("/home");
      })
      .catch((error) => console.error(error));
  };

  return (
    <div className="d-flex justify-content-center align-items-center bg-secondary vh-100">
      <div className="bg-white p-2   rounded vw-50">
        <h2>Enter your details</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3 d-flex justify-content-left">
            <label>
              <strong>What is your current company name?</strong>
              <input
                type="text"
                placeholder="Enter Company Name"
                autoComplete="off"
                name="company"
                className="form-control rounded-0"
                onChange={(e) => setCompany(e.target.value)}
              />
            </label>
          </div>
          <div className="mb-3 d-flex justify-content-left">
            <label>
              <strong>Which Country are you currently working in:</strong>
              <input
                type="text"
                placeholder="Enter Country Name"
                autoComplete="off"
                name="country"
                className="form-control rounded-0"
                onChange={(e) => setCountry(e.target.value)}
              />
            </label>
          </div>
          <div className="mb-3 d-flex justify-content-left">
            <label>
              <strong>Which City are you currently residing in:</strong>
              <input
                type="text"
                placeholder="Enter City Name"
                autoComplete="off"
                name="city"
                className="form-control rounded-0"
                onChange={(e) => setCity(e.target.value)}
              />
            </label>
          </div>

          <div className="mb-3 d-flex justify-content-left">
            <label>
              <strong>What is your salary? (in Euros)</strong>

              <input
                type="text"
                autoComplete="off"
                name="salary"
                className="form-control rounded-0"
                placeholder="In Euros"
                onChange={(e) => setSalary(e.target.value)}
              />
            </label>
          </div>

          <button type="submit" className="btn btn-success w-100 rounded-0">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}

export default Form;
